mapper.select:
select * from xyfw_goods
where 1=1

    and goods_id = #{goods_id}

select assess_id,content as gacontent,pic as gapic from xyfw_goodsassess
where 1=1

    and goods_id = #{goods_id}

select * from xyfw_goods
where 1=1

    and user_id = #{user_id}


    and goods_id = #{goods_id}

limit 1

mapper.update:
update xyfw_goods set

    state = #{state}

where goods_id = #{goods_id}
